import React, { useState } from 'react';
import ReactDOM from 'react-dom/client';
import axios from 'axios';

function Form() {
  const url = "http://localhost:8080/array"

  const [produto, setProduto] = useState('');

  const handleSubmit = (event) => {
    
    fetch(url, {
            method: 'POST',
            headers: {
                'Content-type': 'application/json',
            },
            body: JSON.stringify({
                produto,
             }),
        }).then(response => {
            console.log(response)
            return response.json()
        }).then(json => {
            console.log(json)
            return json
        })
        .catch(err => console.log(err))
        event.preventDefault();
    }
  

  return(
    <div className="col-xl-12 col-lg-7">
    <div className="card shadow mb-4">
      <div className="card-body">
        <form className="user" onSubmit={handleSubmit}>
          <div className="form-group">
            <div className="row">
              
              <div className="col-sm-4 mb-3 mb-sm-4">
                <input type="text" className="form-control form-control-user" id="" placeholder="Nome do Produto" value={produto} onChange={({ target }) => setProduto(target.value)}/>
              </div>
              
            </div>
            <button type="submit" className="btn btn-primary btn-user col-sm-4 mb-3 mb-sm-4"> Cadastrar Produto </button>
          </div>
        </form>
      </div>
    </div>
  </div>
  );
}
  
  
export default Form;
