import React, { Component } from 'react';
import axios from "axios"; 

class Form extends Component {
  state = {
    string: ''
  };
  handleSubmit = event => {
    event.preventDefault();
    const array = {stringArray: this.state.string}
    const url = 'http://localhost:8080/array'
	
	axios({method: 'post',
	url: url,
	headers: {},
	data: array })
      .then(res=>{
        console.log(res);
        console.log(res.data);
      })
  }
  handleResult = event => {
    event.preventDefault();
    const url = 'http://localhost:8080/array'
	const [post, setPost] = React.useState(null);
    
    axios.get(url).then((response) => {
      //setPost(response.data);
	  console.log(response.data);
  })
  if (!post) return null;
  }

handleChange = event =>{
    this.setState({ string: event.target.value});
  }

render() {
    return (
      <div>
        <form>
            <input type = "text" placeholder="Array" className="form-control" onChange= {this.handleChange}/>
          <button type = "submit" onClick={this.handleSubmit} className="btn btn-primary"> Enviar </button>
		  <button type = "submit" onClick={this.handleResult} className="btn btn-secondary"> Receber </button>
        </form>
    </div>
    );
  }
}
export default Form;
